package mong.testroom2;class MessageSendingThread extends Thread {	String message;	public MessageSendingThread(String str, int priority) {		message = str;		setPriority(priority);	}		@Override	public void run() {		for(int i=0; i<100; i++)			System.out.println(message + "(" + getPriority() + ")");	}}public class PriorityTestOne {	public static void main(String[] args) {		MessageSendingThread tr1 = new MessageSendingThread("First", Thread.MAX_PRIORITY);		MessageSendingThread tr2 = new MessageSendingThread("Second", Thread.MIN_PRIORITY);		MessageSendingThread tr3 = new MessageSendingThread("Third", Thread.NORM_PRIORITY);		tr1.start();		tr2.start();		tr3.start();	}}